        let contents = File_fetcher.fetch_exn how_to_fetch ~resource in
        List.iter (get_linked_articles contents) ~f:print_endline]
;;

module G = Graph.Imperative.Graph.Concrete (String)

module Dot = Graph.Graphviz.Dot (struct
    include G

    let edge_attributes _ = [ `Dir `Forward ]
    let default_edge_attributes _ = []
    let get_subgraph _ = None
    let vertex_attributes v = [ `Shape `Box; `Label v; `Fillcolor 1000 ]
    let vertex_name v = Printf.sprintf "\"%s\"" v
    let default_vertex_attributes _ = []
    let graph_attributes _ = []
  end)

let get_html_as_string url how_to_fetch : string =
  File_fetcher.fetch_exn how_to_fetch ~resource:url
;;

let get_title contents : string =
  let open Soup in
  parse contents $ "title" |> R.leaf_text
;;

let enqueue_new_neighbor ~visited ~to_visit neighbor : unit =
  if not (Hash_set.mem visited neighbor) then Queue.enqueue to_visit neighbor
;;

let remove_wiki_prefix url =
  String.substr_replace_first url ~pattern:"/wiki/" ~with_:""
;;

let url_to_title url =
  remove_wiki_prefix url
  |> String.substr_replace_first
       ~pattern:"https://en.wikipedia.org"
       ~with_:""
;;

(* don't check for existence because we want to add as many edges as possible but we will not enqueue visited nodes in the enqueue_new_neighbor function *)
let add_new_edges ~graph ~html_contents ~how_to_fetch outgoing_link : unit =
  ignore how_to_fetch;
  let neighbor_title = url_to_title outgoing_link in
  let source_title = get_title html_contents in
  G.add_edge graph source_title neighbor_title
;;

let fix_url_if_needed url how_to_fetch =
  match (how_to_fetch : File_fetcher.How_to_fetch.t) with
  | Local _ -> url
  | Remote -> Printf.sprintf "https://en.wikipedia.org%s" url
;;

(* enqueue-ing an empty string represents an invalid link, which can act as a delimeter to detect a new frontier *)
let filter_and_enqueue
      ~(graph : G.t)
      ~(html_contents : string)
      ~(outgoing_links : string list)
      ~(visited : File_fetcher.Resource.Hash_set.t)
      ~(to_visit : string Base.Queue.t)
      ~(how_to_fetch : File_fetcher.How_to_fetch.t)
  : unit
  =
  List.iter outgoing_links ~f:(fun outgoing_link ->
    let formatted_link = fix_url_if_needed outgoing_link how_to_fetch in
    (* if not (Hash_set.mem visited formatted_link) *)
    (* then ( *)
    add_new_edges
      ~graph
      ~html_contents
      ~how_to_fetch
      (remove_wiki_prefix outgoing_link);
    enqueue_new_neighbor ~visited ~to_visit formatted_link);
  Queue.enqueue to_visit ""
;;

(* [visualize] should explore all linked articles up to a distance of [max_depth] away
   from the given [origin] article, and output the result as a DOT file. It should use the
   [how_to_fetch] argument along with [File_fetcher] to fetch the articles so that the
   implementation can be tested locally on the small dataset in the ../resources/wiki
   directory. *)
let visualize ?(max_depth = 3) ~origin ~output_file ~how_to_fetch () : unit =
  let current_depth : int ref = ref 0 in
  let visited = String.Hash_set.create () in
  (* let visited = Hash_set.create (module String) in *)
  let to_visit = Queue.create () in
  let graph = G.create () in
  Queue.enqueue to_visit origin;
  Queue.enqueue to_visit "";
  let rec traverse_neighbors () =
    match Queue.dequeue to_visit with
    | None -> ()
    | Some url_to_visit ->
      if String.equal url_to_visit ""
      then incr current_depth
      else if not (Hash_set.mem visited url_to_visit)
      then
        if current_depth.contents <= max_depth
        then (
          Hash_set.add visited url_to_visit;
          let html_contents = get_html_as_string url_to_visit how_to_fetch in
          let outgoing_links = get_linked_articles html_contents in
          filter_and_enqueue
            ~graph
            ~html_contents
            ~outgoing_links
            ~visited
            ~to_visit
            ~how_to_fetch);
      traverse_neighbors ()
  in
  traverse_neighbors ();
  Dot.output_graph
    (Out_channel.create (File_path.to_string output_file))
    graph
;;

let visualize_command =
  let open Command.Let_syntax in
  Command.basic
    ~summary:
      "parse a file listing interstates and generate a graph visualizing \
       the highway network"
    [%map_open
      let how_to_fetch = File_fetcher.How_to_fetch.param
      and origin = flag "origin" (required string) ~doc:" the starting page"
      and max_depth =
        flag
          "max-depth"
          (optional_with_default 2 int)
          ~doc:"INT maximum length of path to search for (default 10)"
      and output_file =
        flag
          "output"
          (required File_path.arg_type)
          ~doc:"FILE where to write generated graph"
      in
      fun () ->
        visualize ~max_depth ~origin ~output_file ~how_to_fetch ();
        printf !"Done! Wrote dot file to %{File_path}\n%!" output_file]
;;

(* [find_path] should attempt to find a path between the origin article and the
   destination article via linked articles.

   [find_path] should use the [how_to_fetch] argument along with [File_fetcher] to fetch
   the articles so that the implementation can be tested locally on the small dataset in
   the ../resources/wiki directory.

   [max_depth] is useful to limit the time the program spends exploring the graph. *)
let find_path ?(max_depth = 3) ~origin ~destination ~how_to_fetch () =
  let current_depth : int ref = ref 0 in
  let visited = String.Hash_set.create () in
  (* let visited = Hash_set.create (module String) in *)
  let to_visit = Queue.create () in
  let graph = G.create () in
  Queue.enqueue to_visit origin;
  Queue.enqueue to_visit "";
  let rec traverse_neighbors () =
    match Queue.dequeue to_visit with
    | None -> ()
    | Some url_to_visit ->
      if String.equal url_to_visit ""
      then incr current_depth
      else if not (Hash_set.mem visited url_to_visit)
      then
        if current_depth.contents <= max_depth
        then (
          Hash_set.add visited url_to_visit;
          let html_contents = get_html_as_string url_to_visit how_to_fetch in
          let outgoing_links = get_linked_articles html_contents in
          filter_and_enqueue
            ~graph
            ~html_contents
            ~outgoing_links
            ~visited
            ~to_visit
            ~how_to_fetch);
      traverse_neighbors ()
  in
  traverse_neighbors ();
  ignore destination;
  None
;;

let find_path_command =
  let open Command.Let_syntax in
  Command.basic
    ~summary:
      "Play wiki game by finding a link between the origin and destination \
       pages"
    [%map_open
      let how_to_fetch = File_fetcher.How_to_fetch.param
      and origin = flag "origin" (required string) ~doc:" the starting page"
      and destination =
        flag "destination" (required string) ~doc:" the destination page"
      and max_depth =
        flag
          "max-depth"
          (optional_with_default 3 int)
          ~doc:"INT maximum length of path to search for (default 10)"
      in
      fun () ->
        match find_path ~max_depth ~origin ~destination ~how_to_fetch () with
        | None -> print_endline "No path found!"
        | Some trace -> List.iter trace ~f:print_endline]
;;

let command =
  Command.group
    ~summary:"wikipedia game commands"
    [ "print-links", print_links_command
    ; "visualize", visualize_command
    ; "find-path", find_path_command
    ]
